wwv_flow_api.create_flow_process(
 p_id=>wwv_flow_api.id(24291364798083531)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'run_qry'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE',
'   l_excel BLOB;',
'   l_col_count PLS_INTEGER;',
'BEGIN',
'   IF APEX_APPLICATION.g_x04 = ''IR''',
'   THEN',
'      dq.Get_Query_Result_Ir (APEX_APPLICATION.g_x01,',
'                              APEX_APPLICATION.g_x03,',
'                              APEX_APPLICATION.g_x04);',
'',
'      l_col_count := 0;',
'',
'',
'      FOR l_col_count IN 1 .. 20',
'      LOOP',
'         APEX_UTIL.set_session_state (''P10_COL_HEAD'' || l_col_count, NULL);',
'      END LOOP;',
'',
'      l_col_count := 0;',
'',
'      FOR curCols IN (  SELECT dqc.name',
'                          FROM dq_queries dq, TABLE (dq.query_columns) dqc',
'                         WHERE dq.query_id = APEX_APPLICATION.g_x01',
'                      ORDER BY dqc.id)',
'      LOOP',
'         l_col_count := l_col_count + 1;',
'         APEX_UTIL.set_session_state (''P10_COL_HEAD'' || l_col_count,',
'                                      REPLACE (curCols.name, ''_'', '' ''));',
'      END LOOP;',
'   ELSE',
'      --x01 query_id',
'      --x02 query_name',
'      --x03 values',
'      --x04 query_result_format_id',
'',
'      l_excel :=',
'         dq.Get_Query_Result (APEX_APPLICATION.g_x01,',
'                              APEX_APPLICATION.g_x03,',
'                              APEX_APPLICATION.g_x04);',
'',
'      IF APEX_APPLICATION.g_x04 = ''XHTML'' OR APEX_APPLICATION.g_x04 = ''WHTML''',
'      THEN',
'         OWA_UTIL.mime_header (''text/html'', FALSE);',
'         HTP.p (''Content-Length: '' || DBMS_LOB.getlength (l_excel));',
'         OWA_UTIL.http_header_close;',
'      ELSE',
'         HTP.p (',
'               ''Content-Type: application/''',
'            || LOWER (APEX_APPLICATION.g_x04)',
'            || ''; charset=UTF-8'');',
'         HTP.p (',
'               ''Content-Disposition: inline; filename="''',
'            || APEX_APPLICATION.g_x02',
'            || ''.''',
'            || LOWER (APEX_APPLICATION.g_x04)',
'            || ''";'');',
'         HTP.p (''Content-Length: '' || DBMS_LOB.getlength (l_excel));',
'         HTP.p (''Cache-Control: "no-cache"'');',
'         HTP.p (''Set-Cookie: fileDownload=true; path=/'');',
'         HTP.p ('''');',
'      END IF;',
'',
'',
'',
'      WPG_DOCLOAD.download_file (l_excel);',
'      HTMLDB_APPLICATION.g_unrecoverable_error := TRUE;',
'   END IF;',
'END;'))
,p_security_scheme=>'MUST_NOT_BE_PUBLIC_USER'
);
end;
